\";s:21:\"create user wordpress\";s:4:\"code\";s:1154:\"// Thm m x l to ti khon ngi dng vo file functions.php ca theme hoc trong mt plugin ty chnh\r\nadd_action( \'wpcf7_before_send_mail\', \'create_user_from_registration_form\' );\r\nfunction create_user_from_registration_form( $contact_form ) {\r\n    // Ly d liu t form\r\n    $submission = WPCF7_Submission::get_instance();\r\n    if ( $submission ) {\r\n        $posted_data = $submission->get_posted_data();\r\n\r\n        // Kim tra nu c d liu v nt gi  c nhn\r\n        if ( isset( $posted_data[\'username\'] ) && isset( $posted_data[\'email\'] ) && isset( $posted_data[\'password-10\'] ) ) {\r\n            // To ti khon ngi dng mi vi vai tr l thnh vin ng k\r\n            $user_id = wp_create_user( $posted_data[\'username\'], $posted_data[\'password-10\'], $posted_data[\'email\'] );			\r\n            // Kim tra nu ti khon  c to thnh cng\r\n            if (!is_wp_error($user_id) ) {               \r\n                wp_redirect(home_url()); // Chuyn hng v trang ch\r\n                exit;\r\n            }\r\n        }\r\n    }\r\n}\r\n\";s:9:\"code_type\";s:3:\"php\";s:8:\"location\";s:10:\"everywhere\";s:11:\"auto_insert\";i:1;s:13:\"insert_number\";i:1;s:9:\"use_rules\";b:0;s:5:\"rules\";a:0:{}s:8:\"priority\";i:10;s:14:\"location_extra\";s:0:\"\";s:20:\"shortcode_attributes\";a:0:{}s:8:\"modified\";s:19:\"2024-05-20 09:12:15\";}i:2;a:13:{s:2:\"id\";i:1027;s:5:\"title\";s:26:\"Add Menu Action My Account\";s:4:\"code\";s:3849:\"/*\r\n * Add custom endpoint that appears in My Account Page - WooCommerce 2.6\r\n * New URL below as Claudio changed his github username\r\n * Ref - https://gist.github.com/claudiosanches/a79f4e3992ae96cb821d3b357834a005#file-custom-my-account-endpoint-php\r\n * Lu  : khi thm vo 1 chc nng, vo Permalink Manager nhn lu li.\r\n */\r\n\r\n\r\nclass My_Custom_My_Account_Endpoint {\r\n\r\n	/**\r\n	 * Custom endpoint name.\r\n	 *\r\n	 * @var string\r\n	 */\r\n	//public static $endpoint = \'settings\';\r\n	// public static $titleMenu = \'Settings\';\r\n	private $nameMenu = \'\';\r\n	private $titleMenu = \'\';\r\n	/**\r\n	 * Plugin actions.\r\n	 */\r\n	public function __construct($nameMenu,$titleMenu) {\r\n		// Actions used to insert a new endpoint in the WordPress.\r\n		$this->nameMenu = $nameMenu;\r\n		$this->titleMenu = $titleMenu;\r\n		add_action( \'init\', array( $this, \'add_endpoints\' ) );\r\n		add_filter( \'query_vars\', array( $this, \'add_query_vars\' ), 0 );\r\n\r\n		// Change the My Accout page title.\r\n		add_filter( \'the_title\', array( $this, \'endpoint_title\' ) );\r\n\r\n		// Insering your new tab/page into the My Account page.\r\n		add_filter( \'woocommerce_account_menu_items\', array( $this, \'new_menu_items\' ) );\r\n		add_action( \'woocommerce_account_\' . $nameMenu .  \'_endpoint\', array( $this, \'endpoint_content\' ) );\r\n	}\r\n\r\n	/**\r\n	 * Register new endpoint to use inside My Account page.\r\n	 *\r\n	 * @see https://developer.wordpress.org/reference/functions/add_rewrite_endpoint/\r\n	 */\r\n	public function add_endpoints() {\r\n		add_rewrite_endpoint($this->nameMenu,EP_ALL);\r\n	}\r\n\r\n	/**\r\n	 * Add new query var.\r\n	 *\r\n	 * @param array $vars\r\n	 * @return array\r\n	 */\r\n	public function add_query_vars( $vars ) {\r\n		$vars[] = $this->nameMenu;\r\n\r\n		return $vars;\r\n	}\r\n\r\n	/**\r\n	 * Set endpoint title.\r\n	 *\r\n	 * @param string $title\r\n	 * @return string\r\n	 */\r\n	public function endpoint_title( $title ) {\r\n		global $wp_query;\r\n\r\n		$is_endpoint = isset( $wp_query->query_vars[$this->nameMenu] );\r\n\r\n		if ( $is_endpoint && ! is_admin() && is_main_query() && in_the_loop() && is_account_page() ) {\r\n			// New page title.\r\n			$title = __($this->titleMenu, \'woocommerce\' );\r\n\r\n			remove_filter( \'the_title\', array( $this, \'endpoint_title\' ) );\r\n		}\r\n\r\n		return $title;\r\n	}\r\n\r\n	/**\r\n	 * Insert the new endpoint into the My Account menu.\r\n	 *\r\n	 * @param array $items\r\n	 * @return array\r\n	 */\r\n	public function new_menu_items( $items ) {\r\n		// Remove the logout menu item.\r\n		$logout = $items[\'customer-logout\'];\r\n		unset( $items[\'customer-logout\'] );\r\n		// Insert your custom endpoint.\r\n		$items[$this->nameMenu] = __($this->titleMenu, \'woocommerce\' );\r\n\r\n		// Insert back the logout item.\r\n		$items[\'customer-logout\'] = $logout;\r\n\r\n		return $items;\r\n	}\r\n\r\n	/**\r\n	 * Endpoint HTML content.\r\n	 */\r\n	public function endpoint_content() {		 \r\n		 $file_path = WC()->plugin_path() . \'/templates/myaccount/\'.$this->nameMenu.\'.php\';\r\n			if (file_exists($file_path)) {				\r\n				include $file_path;		\r\n			}else{\r\n				$file_to_copy = WC()->plugin_path() . \'/templates/myaccount/template.php\';\r\n				if(copy($file_to_copy, $file_path) ) {\r\n					echo \"File  c copy thnh cng!\";\r\n					echo \"Vui lng cp nht code theo ng dn sau:\".$file_path;\r\n				} else {\r\n					echo \"C li xy ra khi thc hin copy file.\";\r\n				}\r\n			}\r\n	}\r\n\r\n	/**\r\n	 * Plugin install action.\r\n	 * Flush rewrite rules to make our custom endpoint available.\r\n	 */\r\n	public static function install() {\r\n		flush_rewrite_rules();\r\n	}\r\n}\r\n\r\nnew My_Custom_My_Account_Endpoint(\'settings\',\'Ci t\');\r\nnew My_Custom_My_Account_Endpoint(\'report\',\'Bo co\');\r\nnew My_Custom_My_Account_Endpoint(\'wall\',\'V ca ti\');\r\nnew My_Custom_My_Account_Endpoint(\'info\',\'Thng tin\');\r\n// Flush rewrite rules on plugin activation.\r\nregister_activation_hook( __FILE__, array( \'My_Custom_My_Account_Endpoint\', \'install\' ) );\";s:9:\"code_type\";s:3:\"php\";s:8:\"location\";s:10:\"everywhere\";s:11:\"auto_insert\";i:1;s:13:\"insert_number\";i:1;s:9:\"use_rules\";b:0;s:5:\"rules\";a:0:{}s:8:\"priority\";i:10;s:14:\"location_extra\";s:0:\"\";s:20:\"shortcode_attributes\";a:0:{}s:8:\"modified\";s:19:\"2024-05-29 03:31:46\";}i:3;a:13:{s:2:\"id\";i:955;s:5:\"title\";s:19:\"Quick View Viet Hoa\";s:4:\"code\";s:374:\"function 