_action( \'wp_head\', \'wp_oembed_add_host_js\' );\\r\\n\\tadd_filter( \'tiny_mce_plugins\', function ( $plugins ) {\\r\\n\\t\\treturn array_diff( $plugins, array( \'wpembed\' ) );\\r\\n\\t} );\\r\\n\\r\\n\\t\\/\\/ Remove all embeds rewrite rules.\\r\\n\\tadd_filter( \'rewrite_rules_array\', function ( $rules ) {\\r\\n\\t\\tforeach ( $rules as $rule => $rewrite ) {\\r\\n\\t\\t\\tif ( false !== strpos( $rewrite, \'embed=true\' ) ) {\\r\\n\\t\\t\\t\\tunset( $rules[ $rule ] );\\r\\n\\t\\t\\t}\\r\\n\\t\\t}\\r\\n\\r\\n\\t\\treturn $rules;\\r\\n\\t} );\\r\\n\\r\\n\\t\\/\\/ Remove filter of the oEmbed result before any HTTP requests are made.\\r\\n\\tremove_filter( \'pre_oembed_result\', \'wp_filter_pre_oembed_result\', 10 );\\r\\n}, 9999 );\",\"note\":\"Remove an extra request and prevent others from adding embeds in your site.\",\"categories\":[\"disable\"],\"code_type\":\"php\",\"needs_auth\":false},{\"library_id\":897,\"title\":\"Disable Emojis\",\"code\":\"\\/**\\r\\n * Disable the emojis in WordPress.\\r\\n *\\/\\r\\nadd_action( \'init\', function () {\\r\\n\\tremove_action( \'wp_head\', \'print_emoji_detection_script\', 7 );\\r\\n\\tremove_action( \'admin_print_scripts\', \'print_emoji_detection_script\' );\\r\\n\\tremove_action( \'wp_print_styles\', \'print_emoji_styles\' );\\r\\n\\tremove_action( \'admin_print_styles\', \'print_emoji_styles\' );\\r\\n\\tremove_filter( \'the_content_feed\', \'wp_staticize_emoji\' );\\r\\n\\tremove_filter( \'comment_text_rss\', \'wp_staticize_emoji\' );\\r\\n\\tremove_filter( \'wp_mail\', \'wp_staticize_emoji_for_email\' );\\r\\n\\r\\n\\t\\/\\/ Remove from TinyMCE.\\r\\n\\tadd_filter( \'tiny_mce_plugins\', function ( $plugins ) {\\r\\n\\t\\tif ( is_array( $plugins ) ) {\\r\\n\\t\\t\\treturn array_diff( $plugins, array( \'wpemoji\' ) );\\r\\n\\t\\t} else {\\r\\n\\t\\t\\treturn array();\\r\\n\\t\\t}\\r\\n\\t} );\\r\\n\\r\\n\\t\\/\\/ Remove from dns-prefetch.\\r\\n\\tadd_filter( \'wp_resource_hints\', function ( $urls, $relation_type ) {\\r\\n\\t\\tif ( \'dns-prefetch\' === $relation_type ) {\\r\\n\\t\\t\\t$emoji_svg_url = apply_filters( \'emoji_svg_url\', \'https:\\/\\/s.w.org\\/images\\/core\\/emoji\\/2\\/svg\\/\' );\\r\\n\\t\\t\\t$urls          = array_diff( $urls, array( $emoji_svg_url ) );\\r\\n\\t\\t}\\r\\n\\r\\n\\t\\treturn $urls;\\r\\n\\t}, 10, 2 );\\r\\n} );\\r\\n\",\"note\":\"Disable Emoji\'s in WordPress to improve your site\'s performance\",\"categories\":[\"disable\"],\"code_type\":\"php\",\"needs_auth\":false},{\"library_id\":15146,\"title\":\"Disable Full Site Editing (FSE)\",\"code\":\"add_action( \'admin_menu\', function() {\\r\\n\\tremove_submenu_page(\'themes.php\', \'site-editor.php\');\\r\\n});\\r\\nadd_action(\'admin_bar_menu\', function($wp_admin_bar) {\\r\\n\\t$wp_admin_bar->remove_node(\'site-editor\');\\r\\n}, 250);\\r\\nadd_action(\'admin_init\', function() {\\r\\n\\tglobal $pagenow;\\r\\n\\tif (\'site-editor.php\' === $pagenow) {\\r\\n\\t\\twp_safe_redirect(admin_url()); \\r\\n\\t\\texit;\\r\\n\\t}\\r\\n});\",\"note\":\"Prevent any user from making changes to the site using the Full Site Editor.\",\"categories\":[\"admin\",\"disable\"],\"code_type\":\"php\",\"needs_auth\":false},{\"library_id\":13023,\"title\":\"Disable Fullscreen Editor\",\"code\":\"add_action(\\r\\n\\t\'enqueue_block_editor_assets\',\\r\\n\\tfunction () {\\r\\n\\t\\t$script = \\\"jQuery( window ).load(function() { const isFullscreenMode = wp.data.select( \'core\\/edit-post\' ).isFeatureActive( \'fullscreenMode\' ); if ( isFullscreenMode ) { wp.data.dispatch( \'core\\/edit-post\' ).toggleFeature( \'fullscreenMode\' ); } });\\\";\\r\\n\\t\\twp_add_inline_script( \'wp-blocks\', $script );\\r\\n\\t}\\r\\n);\",\"note\":\"Automatically disable the Gutenberg Fullscreen editor for all users.\",\"categories\":[\"admin\",\"disable\"],\"code_type\":\"php\",\"needs_auth\":false},{\"library_id\":1193,\"title\":\"Disable Gutenberg Code Editing for Non-Admin Users\",\"code\":\"add_filter( \'block_editor_settings_all\', function ( $settings ) {\\r\\n\\t\\r\\n\\t$settings[\'codeEditingEnabled\'] = current_user_can( \'manage_options\' );\\r\\n\\r\\n\\treturn $settings;\\r\\n} );\",\"note\":\"Prevent non-admin users from using \\\"Edit as HTML\\\" or \\\"Code editor\\\" in the Gutenberg Editor.\",\"categories\":[\"admin\",\"disable\"],\"code_type\":\"php\",\"needs_auth\":false},{\"library_id\":14,\"title\":\"Disable Gutenberg Editor (use Classic Editor)\",\"code\":\"add_filter(\'gutenberg_can_edit_post\', \'__return_false\', 5);\\r\\nadd_filter(\'use_block_editor_for_post\', \'__return_false\', 5);\",\"note\":\"Switch back to the Classic Editor by disablling the Block Editor.\",\"categories\":[\"most-popular\",\"admin\"],\"code_type\":\"php\",\"needs_auth\":false},{\"library_id\":17253,\"title\":\"Disable jQuery Migrate\",\"code\":\"add_action( \'wp_default_scripts\', function ( $scripts ) {\\r\\n\\tif ( ! is_admin() && isset( $scripts->registered[\'jquery\'] ) ) {\\r\\n\\t\\t$script = $scripts->registered[\'jquery\'];\\r\\n\\t\\tif ( ! empty( $script->deps ) ) {\\r\\n\\t\\t\\t$script->deps = array_diff( $script->deps, array( \'jquery-migrate\' ) );\\r\\n\\t\\t}\\r\\n\\t}\\r\\n}, 150 );\",\"note\":\"Prevent loading the jQuery Migrate script in the frontend.\",\"categories\":[\"disable\"],\"code_type\":\"php\",\"needs_auth\":false},{\"library_id\":6004,\"title\":\"Disable Lazy Load\",\"code\":\"add_filter( \'wp_lazy_loading_enabled\', \'__return_false\' );\\r\\n\",\"note\":\"Prevent WordPress from adding the lazy-load attribute to all images and iframes.\",\"categories\":[\"attachments\",\"disable\"],\"code_type\":\"php\",\"needs_auth\":false},{\"library_id\":13027,\"title\":\"Disable Login Autofocus\",\"code\":\"add_filter( \'enable_login_autofocus\', \'__return_false\' );\",\"note\":\"Prevent autofocus on the username field on the login page.\",\"categories\":[\"disable\",\"login\"],\"code_type\":\"php\",\"needs_auth\":false},{\"library_id\":46,\"title\":\"Disable Login by Email