"categories\":[\"admin\",\"disable\"],\"code_type\":\"php\",\"needs_auth\":false},{\"library_id\":6011,\"title\":\"Disable Plugin & Theme Editor\",\"code\":\"\\/\\/ Disable the Plugin and Theme Editor\\r\\nif ( ! defined( \'DISALLOW_FILE_EDIT\' ) ) {\\r\\n\\tdefine( \'DISALLOW_FILE_EDIT\', true );\\r\\n}\\r\\n\",\"note\":\"Prevent users from using the Plugin & Theme file editor.\",\"categories\":[\"admin\",\"disable\"],\"code_type\":\"php\",\"needs_auth\":false},{\"library_id\":15144,\"title\":\"Disable Posts in Admin\",\"code\":\"add_action( \'admin_bar_menu\', function ( $wp_admin_bar ) {\\r\\n\\t$wp_admin_bar->remove_node( \'new-post\' );\\r\\n}, 100 );\\r\\n\\r\\nadd_action( \'admin_menu\', function () {\\r\\n\\tremove_menu_page( \'edit.php\' );\\r\\n} );\",\"note\":\"Hide the Posts menu from the admin if you don\'t need the default Posts post...\",\"categories\":[\"admin\",\"disable\"],\"code_type\":\"php\",\"needs_auth\":false},{\"library_id\":6568,\"title\":\"Disable REST API Links\",\"code\":\"remove_action( \'xmlrpc_rsd_apis\', \'rest_output_rsd\' );\\r\\nremove_action( \'wp_head\', \'rest_output_link_wp_head\' );\\r\\nremove_action( \'template_redirect\', \'rest_output_link_header\', 11 );\",\"note\":\"Remove all head links to the REST API endpoints from the page source.\",\"categories\":[\"disable\"],\"code_type\":\"php\",\"needs_auth\":false},{\"library_id\":6564,\"title\":\"Disable RSS Feed Links\",\"code\":\"remove_action(\'wp_head\', \'feed_links\', 2 );\\r\\nremove_action(\'wp_head\', \'feed_links_extra\', 3 );\",\"note\":\"Remove head links to RSS Feeds from all the pages on your site.\",\"categories\":[\"rss-feeds\"],\"code_type\":\"php\",\"needs_auth\":false},{\"library_id\":41,\"title\":\"Disable RSS Feeds\",\"code\":\"\\/**\\r\\n * Display a custom message instead of the RSS Feeds.\\r\\n *\\r\\n * @return void\\r\\n *\\/\\r\\nfunction wpcode_snippet_disable_feed() {\\r\\n\\twp_die(\\r\\n\\t\\tsprintf(\\r\\n\\t\\t\\t\\/\\/ Translators: Placeholders for the homepage link.\\r\\n\\t\\t\\tesc_html__( \'No feed available, please visit our %1$shomepage%2$s!\' ),\\r\\n\\t\\t\\t\' <a href=\\\"\' . esc_url( home_url( \'\\/\' ) ) . \'\\\">\',\\r\\n\\t\\t\\t\'<\\/a>\'\\r\\n\\t\\t)\\r\\n\\t);\\r\\n}\\r\\n\\r\\n\\/\\/ Replace all feeds with the message above.\\r\\nadd_action( \'do_feed_rdf\', \'wpcode_snippet_disable_feed\', 1 );\\r\\nadd_action( \'do_feed_rss\', \'wpcode_snippet_disable_feed\', 1 );\\r\\nadd_action( \'do_feed_rss2\', \'wpcode_snippet_disable_feed\', 1 );\\r\\nadd_action( \'do_feed_atom\', \'wpcode_snippet_disable_feed\', 1 );\\r\\nadd_action( \'do_feed_rss2_comments\', \'wpcode_snippet_disable_feed\', 1 );\\r\\nadd_action( \'do_feed_atom_comments\', \'wpcode_snippet_disable_feed\', 1 );\\r\\n\\/\\/ Remove links to feed from the header.\\r\\nremove_action( \'wp_head\', \'feed_links_extra\', 3 );\\r\\nremove_action( \'wp_head\', \'feed_links\', 2 );\\r\\n\",\"note\":\"Turn off the WordPress RSS Feeds for your website with 1 click.\",\"categories\":[\"rss-feeds\"],\"code_type\":\"php\",\"needs_auth\":false},{\"library_id\":47,\"title\":\"Disable Search\",\"code\":\"\\/\\/ Prevent search queries.\\r\\nadd_action(\\r\\n\\t\'parse_query\',\\r\\n\\tfunction ( $query, $error = true ) {\\r\\n\\t\\tif ( is_search() && ! is_admin() ) {\\r\\n\\t\\t\\t$query->is_search       = false;\\r\\n\\t\\t\\t$query->query_vars[\'s\'] = false;\\r\\n\\t\\t\\t$query->query[\'s\']      = false;\\r\\n\\t\\t\\tif ( true === $error ) {\\r\\n\\t\\t\\t\\t$query->is_404 = true;\\r\\n\\t\\t\\t}\\r\\n\\t\\t}\\r\\n\\t},\\r\\n\\t15,\\r\\n\\t2\\r\\n);\\r\\n\\r\\n\\/\\/ Remove the Search Widget.\\r\\nadd_action(\\r\\n\\t\'widgets_init\',\\r\\n\\tfunction () {\\r\\n\\t\\tunregister_widget( \'WP_Widget_Search\' );\\r\\n\\t}\\r\\n);\\r\\n\\r\\n\\/\\/ Remove the search form.\\r\\nadd_filter( \'get_search_form\', \'__return_empty_string\', 999 );\\r\\n\\r\\n\\/\\/ Remove the core search block.\\r\\nadd_action(\\r\\n\\t\'init\',\\r\\n\\tfunction () {\\r\\n\\t\\tif ( ! function_exists( \'unregister_block_type\' ) || ! class_exists( \'WP_Block_Type_Registry\' ) ) {\\r\\n\\t\\t\\treturn;\\r\\n\\t\\t}\\r\\n\\t\\t$block = \'core\\/search\';\\r\\n\\t\\tif ( WP_Block_Type_Registry::get_instance()->is_registered( $block ) ) {\\r\\n\\t\\t\\tunregister_block_type( $block );\\r\\n\\t\\t}\\r\\n\\t}\\r\\n);\\r\\n\\r\\n\\/\\/ Remove admin bar menu search box.\\r\\nadd_action(\\r\\n\\t\'admin_bar_menu\',\\r\\n\\tfunction ( $wp_admin_bar ) {\\r\\n\\t\\t$wp_admin_bar->remove_menu( \'search\' );\\r\\n\\t},\\r\\n\\t11\\r\\n);\\r\\n\",\"note\":\"Completely disable search on your WordPress website.\",\"categories\":[\"disable\"],\"code_type\":\"php\",\"needs_auth\":false},{\"library_id\":955,\"title\":\"Disable Self Pingbacks\",\"code\":\"add_action( \'pre_ping\', function( &$links ) {\\r\\n\\t$home = get_option( \'home\' );\\r\\n\\tforeach ( $links as $l => $link ) {\\r\\n\\t\\tif ( 0 === strpos( $link, $home ) ) {\\r\\n\\t\\t\\tunset( $links[ $l ] );\\r\\n\\t\\t}\\r\\n\\t}\\r\\n} );\\r\\n\",\"note\":\"Prevent WordPress from automatically creating pingbacks from your own site.\",\"categories\":[\"disable\"],\"code_type\":\"php\",\"needs_auth\":false},{\"library_id\":13595,\"title\":\"Disable Site Admin Email Verification\",\"code\":\"add_filter( \'admin_email_check_interval\', \'__return_false\' );\\r\\n\",\"note\":\"Stop WordPress from asking you to confirm the administrator email address when logging in.\",\"categories\":[\"admin\"],\"code_type\":\"php\",\"needs_auth\":false},{\"library_id\":6006,\"title\":\"Disable Site Health\",\"code\":\"\\/\\/ Remove Tools Submenu Item for Site Health.\\r\\nadd_action( \'admin_menu\', function () {\\r\\n\\tremove_submenu_page( \'tools.php\', \'site-health.php\' );\\r\\n} );\\r\\n\\r\\n\\/\\/ Prevent direct access to the Site Health page.\\r\\nadd_action( \'current_screen\', function () {\\r\\n\\t$screen = get_current_screen();\\r\\n\\tif ( \'site-health\' === $screen->id ) {\\r\\n\\t\\twp_safe_redirect( admin_url() );\\r\\n\\t\\texit;\\r\\n\\t}\\r\\n} );\\r\\n\\r\\n\\/\\/ Disable the Site Health Dashboard Widget.\\r\\nadd_action( \'wp_dashboard_setup\', function () {\\r\\n\\tglobal $wp_meta_boxes;\\r\\n\\tif ( isset( $wp_meta_boxes[\'dashboard\'][\'normal\'][\'core\'][\'dashboard_site_health\'] ) ) {\\r\\n\\t\\tunset( $wp_meta_boxes[\'dashboard\'][\'normal\'][\'core\'][\'dashboard_site_health\'] );\\r\\n\\t}\\r\\n} );\\r\\n\",\"note\":\"Completely hide the Site Health menu item and dashboard widget.\",\"categories\":[\"admin\",\"disable\"],\"code_type\":\"php\",\"needs_auth\":false},{\"library_id\":18697,\"title\":\"Disable Specific Blocks\",\"code\":\"add_filter( \'allowed_block_types_all\', function ( $allowed_block_types, $block_editor_context ) {\\r\\n\\r\\n\\t\\/\\/ List here the blocks you want to disallow. https:\\/\\/developer.wordpress.org\\/block-editor\\/reference-guides\\/core-blocks\\/\\r\\n\\t$disallowed_blocks = array(\\r\\n\\t\\t\'core\\/navigation\',\\r\\n\\t\\t\'core\\/query\',\\r\\n\\t);\\r\\n\\r\\n\\tif ( ! is_array( $allowed_block_types ) || empty( $allowed_block_types ) ) {\\r\\n\\t\\t$registered_blocks   = WP_Block_Type_Registry::get_instance()->get_all_registered();\\r\\n\\t\\t$allowed_block_types = array_keys( $registered_blocks );\\r\\n\\t}\\r\\n\\r\\n\\t$filtered_blocks = array();\\r\\n\\tforeach ( $allowed_block_types as $block ) {\\r\\n\\t\\tif ( ! in_array( $block, $disallowed_blocks, true ) ) {\\r\\n\\t\\t\\t$filtered_blocks[] = $block;\\r\\n\\t\\t}\\r\\n\\t}\\r\\n\\r\\n\\treturn $filtered_blocks;\\r\\n}, 10, 2 );\",\"note\":\"Prevent certain blocks from being used in the Gutenberg Editor.\",\"categories\":[\"admin\",\"disable\"],\"code_type\":\"php\",\"needs_auth\":false},{\"library_id\":15142,\"title\":\"Disable the Excerpt in RSS Feeds\",\"code\":\"\\/\\/ Make sure we display the excerpt in the feed.\\r\\nadd_filter( \'pre_option_rss_use_excerpt\', \'__return_true\' );\\r\\n\\/\\/ Remove the excerpt content for feeds.\\r\\nadd_filter( \'the_excerpt_rss\', \'__return_empty_string\' );\",\"note\":\"Hide the excerpt or content from your RSS feeds.\",\"categories\":[\"rss-feeds\"],\"code_type\":\"php\",\"needs_auth\":false},{\"library_id\":6566,\"title\":\"Disable the WordPress Shortlink\",\"code\":\"remove_action(\'wp_head\', \'wp_shortlink_wp_head\' );\\r\\n\",\"note\":\"Remove link rel shortlink from your site head area.\",\"categories\":[\"archive\",\"disable\"],\"code_type\":\"php\",\"needs_auth\":false},{\"library_id\":42,\"title\":\"Disable The WP Admin Bar\",\"code\":\"\\/* Disable WordPress Admin Bar for all users *\\/\\r\\nadd_filter( \'show_admin_bar\', \'__return_false\' );\",\"note\":\"Hide the WordPress Admin Bar for all users in the frontend.\",\"categories\":[\"most-popular\",\"admin\"],\"code_type\":\"php\",\"needs_auth\":false},{\"library_id\":21345,\"title\":\"Disable Thumbnail Image Sizes\",\"code\":\"add_filter( \'intermediate_image_sizes_advanced\', function( $sizes ) {\\r\\n\\t\\/\\/ Disable specific thumbnail sizes, uncomment the ones you want to disable.\\r\\n\\r\\n\\t\\/\\/ unset( $sizes[\'thumbnail\'] ); \\/\\/ 150px x 150px\\r\\n\\t\\/\\/ unset( $sizes[\'medium\'] ); \\/\\/ 300px x 300px\\r\\n\\t\\/\\/ unset( $sizes[\'medium_large\'] ); \\/\\/ 768px x 0px\\r\\n\\t\\/\\/ unset( $sizes[\'large\'] ); \\/\\/ 1024px x 1024px\\r\\n\\t\\/\\/ unset( $sizes[\'1536x1536\'] ); \\/\\/ 1536px x 1536px\\r\\n\\t\\/\\/ unset( $sizes[\'2048x2048\'] ); \\/\\/ 2048px x 2048px\\r\\n\\t\\r\\n\\treturn $sizes;\\r\\n} );\",\"note\":\"Prevent WordPress from generating specific thumbnail sizes.\",\"categories\":[\"attachments\",\"disable\"],\"code_type\":\"php\",\"needs_auth\":false},{\"library_id\":13029,\"title\":\"Disable Update Notice for Non-admin Users\",\"code\":\"add_action( \'admin_head\', function () {\\r\\n\\tif ( current_user_can( \'update_core\' ) ) {\\r\\n\\t\\treturn;\\r\\n\\t}\\r\\n\\tremove_action( \'admin_notices\', \'update_nag\', 3 );\\r\\n}, 1 );\",\"note\":\"Don\'t show WordPress version update notices to users that can\'t update the site.\",\"categories\":[\"admin\",\"disable\"],\"code_type\":\"php\",\"needs_auth\":false},{\"library_id\":35,\"title\":\"Disable Widget Blocks (use Classic Widgets)\",\"code\":\"add_filter( \'use_widgets_block_editor\', \'__return_false\' );\",\"note\":\"Use the classic interface instead of Blocks to manage Widgets.\",\"categories\":[\"most-popular\",\"widgets\"],\"code_type\":\"php\",\"needs_auth\":false},{\"library_id\":953,\"title\":\"Disable wlwmanifest link\",\"code\":\"remove_action(\'wp_head\', \'wlwmanifest_link\');\\r\\n\",\"note\":\"Prevent WordPress from adding the Windows Live Writer manifest link to your pages.\",\"categories\":[\"disable\"],\"code_type\":\"php\",\"needs_auth\":false},{\"library_id\":21338,\"title\":\"Disable WordPress 6.5 Font Library\",\"code\":\"add_filter( \'block_editor_settings_all\', function( $editor_settings ) {\\r\\n\\t$editor_settings[\'fontLibraryEnabled\'] = false;\\r\\n   \\treturn $editor_settings; \\r\\n} );\\r\\n\\r\\n\\/\\/ Disable the REST API for the font library.\\r\\nadd_filter( \'register_post_type_args\', function( $arg, $post_type ) {\\r\\n\\tif ( \'wp_font_family\' === $post_type || \'wp_font_face\' === $post_type ) {\\r\\n\\t\\t$arg[\'show_in_rest\'] = false;\\r\\n\\t}\\r\\n\\r\\n\\treturn $arg;\\r\\n}, 10, 2 );\\r\\n\\r\\nadd_filter( \'rest_endpoints\', function( $endpoints ) {\\r\\n\\tforeach ( $endpoints as $route => $endpoint ){\\r\\n\\t\\tif ( str_starts_with( $route, \'\\/wp\\/v2\\/font-collections\' ) ) {\\r\\n\\t\\t\\tunset( $endpoints[ $route ] );\\r\\n\\t\\t}\\r\\n\\t}\\r\\n\\r\\n\\treturn $endpoints;\\r\\n} );\",\"note\":\"Deactivate the font library feature added in WordPress 6.5.\",\"categories\":[\"admin\"],\"code_type\":\"php\",\"needs_auth\":false},{\"library_id\":37,\"title\":\"Disable WordPress REST API\",\"code\":\"add_filter(\\r\\n\\t\'rest_authentication_errors\',\\r\\n\\tfunction ( $access ) {\\r\\n\\t\\treturn new WP_Error(\\r\\n\\t\\t\\t\'rest_disabled\',\\r\\n\\t\\t\\t__( \'The WordPress REST API has been disabled.\' ),\\r\\n\\t\\t\\tarray(\\r\\n\\t\\t\\t\\t\'status\' => rest_authorization_required_code(),\\r\\n\\t\\t\\t)\\r\\n\\t\\t);\\r\\n\\t}\\r\\n);\",\"note\":\"Easily disable the WP REST API on your website with this snippet.\",\"categories\":[\"most-popular\",\"disable\"],\"code_type\":\"php\",\"needs_auth\":false},{\"library_id\":6009,\"title\":\"Disable WordPress Sitemaps\",\"code\":\"add_filter(\'wp_sitemaps_enabled\', \'__return_false\');\\r\\n\",\"note\":\"Disable the auto-generated WordPress Sitemaps added in version 5.5.\",\"categories\":[\"disable\"],\"code_type\":\"php\",\"needs_auth\":false},{\"library_id\":55,\"title\":\"Disable XML-RPC\",\"code\":\"add_filter( \'xmlrpc_enabled\', \'__return_false\' );\\r\\n\",\"note\":\"On sites running WordPress 3.5+, disable XML-RPC completely.\",\"categories\":[\"most-popular\",\"disable\"],\"code_type\":\"php\",\"needs_auth\":false},{\"library_id\":16242,\"title\":\"Display Reading Time\",\"code\":\"$reading_speed = 200; \\/\\/ 200 words per minute\\r\\n$content       = get_post_field( \'post_content\', get_the_id() );\\r\\n$word_count    = str_word_count( strip_tags( $content ) );\\r\\n$reading_time  = ceil( $word_count \\/ $reading_speed );\\r\\n\\r\\necho \'<p>Estimated reading time: \' . absint( $reading_time ) . \' \' . _n( \'minute\', \'minutes\', $reading_time ) . \'<\\/p>\';\",\"note\":\"Add the estimated reading time before the post content.\",\"categories\":[\"archive\"],\"code_type\":\"php\",\"needs_auth\":false},{\"library_id\":62,\"title\":\"Display the Last Updated Date\",\"code\":\"$u_time          = get_the_time( \'U\' );\\r\\n$u_modified_time = get_the_modified_time( \'U\' );\\r\\n\\/\\/ Only display modified date if 24hrs have passed since the post was published.\\r\\nif ( $u_modified_time >= $u_time + 86400 ) {\\r\\n\\r\\n\\t$updated_date = get_the_modified_time( \'F jS, Y\' );\\r\\n\\t$updated_time = get_the_modified_time( \'h:i a\' );\\r\\n\\r\\n\\t$updated = \'<p class=\\\"last-updated\\\">\';\\r\\n\\r\\n\\t$updated .= sprintf(\\r\\n\\t\\/\\/ Translators: Placeholders get replaced with the date and time when the post was modified.\\r\\n\\t\\tesc_html__( \'Last updated on %1$s at %2$s\' ),\\r\\n\\t\\t$updated_date,\\r\\n\\t\\t$updated_time\\r\\n\\t);\\r\\n\\t$updated .= \'<\\/p>\';\\r\\n\\r\\n\\techo wp_kses_post( $updated );\\r\\n}\\r\\n\",\"note\":\"Add the last updated date & time to your posts in the frontend.\",\"categories\":[\"archive\"],\"code_type\":\"php\",\"needs_auth\":false},{\"library_id\":1196,\"title\":\"Duplicate Post\\/Page Link\",\"code\":\"\\/\\/ Add duplicate button to post\\/page list of actions.\\r\\nadd_filter( \'post_row_actions\', \'wpcode_snippet_duplicate_post_link\', 10, 2 );\\r\\nadd_filter( \'page_row_actions\', \'wpcode_snippet_duplicate_post_link\', 10, 2 );\\r\\n\\r\\n\\/\\/ Let\'s make sure the function doesn\'t already exist.\\r\\nif ( ! function_exists( \'wpcode_snippet_duplicate_post_link\' ) ) {\\r\\n\\t\\/**\\r\\n\\t * @param array   $actions The actions added as links to the admin.\\r\\n\\t * @param WP_Post $post The post object.\\r\\n\\t *\\r\\n\\t * @return array\\r\\n\\t *\\/\\r\\n\\tfunction wpcode_snippet_duplicate_post_link( $actions, $post ) {\\r\\n\\r\\n\\t\\t\\/\\/ Don\'t add action if the current user can\'t create posts of this post type.\\r\\n\\t\\t$post_type_object = get_post_type_object( $post->post_type );\\r\\n\\r\\n\\t\\tif ( null === $post_type_object || ! current_user_can( $post_type_object->cap->create_posts ) ) {\\r\\n\\t\\t\\treturn $actions;\\r\\n\\t\\t}\\r\\n\\r\\n\\r\\n\\t\\t$url = wp_nonce_url(\\r\\n\\t\\t\\tadd_query_arg(\\r\\n\\t\\t\\t\\tarray(\\r\\n\\t\\t\\t\\t\\t\'action\'  => \'wpcode_snippet_duplicate_post\',\\r\\n\\t\\t\\t\\t\\t\'post_id\' => $post->ID,\\r\\n\\t\\t\\t\\t),\\r\\n\\t\\t\\t\\t\'admin.php\'\\r\\n\\t\\t\\t),\\r\\n\\t\\t\\t\'wpcode_duplicate_post_\' . $post->ID,\\r\\n\\t\\t\\t\'wpcode_duplicate_nonce\'\\r\\n\\t\\t);\\r\\n\\r\\n\\t\\t$actions[\'wpcode_duplicate\'] = \'<a href=\\\"\' . $url . \'\\\" title=\\\"Duplicate item\\\" rel=\\\"permalink\\\">Duplicate<\\/a>\';\\r\\n\\r\\n\\t\\treturn $actions;\\r\\n\\t}\\r\\n}\\r\\n\\r\\n\\/**\\r\\n * Handle the custom action when clicking the button we added above.\\r\\n *\\/\\r\\nadd_action( \'admin_action_wpcode_snippet_duplicate_post\', function () {\\r\\n\\r\\n\\tif ( empty( $_GET[\'post_id\'] ) ) {\\r\\n\\t\\twp_die( \'No post id set for the duplicate action.\' );\\r\\n\\t}\\r\\n\\r\\n\\t$post_id = absint( $_GET[\'post_id\'] );\\r\\n\\r\\n\\t\\/\\/ Check the nonce specific to the post we are duplicating.\\r\\n\\tif ( ! isset( $_GET[\'wpcode_duplicate_nonce\'] ) || ! wp_verify_nonce( $_GET[\'wpcode_duplicate_nonce\'], \'wpcode_duplicate_post_\' . $post_id ) ) {\\r\\n\\t\\t\\/\\/ Display a message if the nonce is invalid, may it expired.\\r\\n\\t\\twp_die( \'The link you followed has expired, please try again.\' );\\r\\n\\t}\\r\\n\\r\\n\\t\\/\\/ Load the post we want to duplicate.\\r\\n\\t$post = get_post( $post_id );\\r\\n\\r\\n\\t\\/\\/ Create a new post data array from the post loaded.\\r\\n\\tif ( $post ) {\\r\\n\\t\\t$current_user = wp_get_current_user();\\r\\n\\t\\t$new_post     = array(\\r\\n\\t\\t\\t\'comment_status\' => $post->comment_status,\\r\\n\\t\\t\\t\'menu_order\'     => $post->menu_order,\\r\\n\\t\\t\\t\'ping_status\'    => $post->ping_status,\\r\\n\\t\\t\\t\'post_author\'    => $current_user->ID,\\r\\n\\t\\t\\t\'post_content\'   => $post->post_content,\\r\\n\\t\\t\\t\'post_excerpt\'   => $post->post_excerpt,\\r\\n\\t\\t\\t\'post_name\'      => $post->post_name,\\r\\n\\t\\t\\t\'post_parent\'    => $post->post_parent,\\r\\n\\t\\t\\t\'post_password\'  => $post->post_password,\\r\\n\\t\\t\\t\'post_status\'    => \'draft\',\\r\\n\\t\\t\\t\'post_title\'     => $post->post_title . \' (copy)\',\\/\\/ Add \\\"(copy)\\\" to the title.\\r\\n\\t\\t\\t\'post_type\'      => $post->p