5','2024-05-17 09:23:15','',837,'https://hamada.vn/?p=1023',0,'revision','',0),(1025,1,'2024-05-19 05:12:12','2024-05-19 05:12:12','function custom_my_account($items){\r\n	$items[\"settings\"] = __(\'Settings\');\r\n	$items[\"report\"] = __(\'Report\');\r\n	return $items;\r\n}\r\nadd_filter(\'woocommerce_account_menu_items\', \'custom_my_account\');\r\nfunction custom_settings_endpoint_content(){	\r\n	$file_path = WC()->plugin_path() . \'/templates/myaccount/settings.php\';\r\n	if (file_exists($file_path)) {\r\n		include $file_path;\r\n	}\r\n}\r\nadd_action(\"woocommerce_account_settings_endpoint\",\"custom_settings_endpoint_content\");\r\nfunction custom_add_settings_endpoint(){	\r\n    add_rewrite_endpoint(\'settings\',EP_ROOT | EP_PAGES);\r\n	add_rewrite_endpoint(\'report\',EP_ROOT | EP_PAGES);	\r\n}\r\nadd_action( \'init\',\'custom_add_settings_endpoint\');','Add Settings My Account','','trash','closed','closed','','add-menu-my-account__trashed','','','2024-05-20 06:36:22','2024-05-20 06:36:22','',0,'https://hamada.vn/?post_type=wpcode&#038;p=1025',0,'wpcode','',0),(1026,1,'2024-05-20 02:56:50','2024-05-20 02:56:50','function custom_report_endpoint_content(){	\r\n	$file_path = WC()->plugin_path() . \'/templates/myaccount/report.php\';\r\n	if (file_exists($file_path)) {\r\n		include $file_path;\r\n	}\r\n}\r\nadd_action(\"woocommerce_account_report_endpoint\",\"custom_report_endpoint_content\");\r\n\r\n','Add Report My Account','','trash','closed','closed','','add-report-my-account__trashed','','','2024-05-20 06:36:22','2024-05-20 06:36:22','',0,'https://hamada.vn/?post_type=wpcode&#038;p=1026',0,'wpcode','',0),(1027,1,'2024-05-20 05:15:58','2024-05-20 05:15:58','/*\r\n * Add custom endpoint that appears in My Account Page - WooCommerce 2.6\r\n * New URL below as Claudio changed his github username\r\n * Ref - https://gist.github.com/claudiosanches/a79f4e3992ae96cb821d3b357834a005#file-custom-my-account-endpoint-php\r\n * Lưu ý : khi thêm vào 1 chức năng, vào Permalink Manager nhấn lưu lại.\r\n */\r\n\r\n\r\nclass My_Custom_My_Account_Endpoint {\r\n\r\n	/**\r\n	 * Custom endpoint name.\r\n	 *\r\n	 * @var string\r\n	 */\r\n	//public static $endpoint = \'settings\';\r\n	// public static $titleMenu = \'Settings\';\r\n	private $nameMenu = \'\';\r\n	private $titleMenu = \'\';\r\n	/**\r\n	 * Plugin actions.\r\n	 */\r\n	public function __construct($nameMenu,$titleMenu) {\r\n		// Actions used to insert a new endpoint in the WordPress.\r\n		$this->nameMenu = $nameMenu;\r\n		$this->titleMenu = $titleMenu;\r\n		add_action( \'init\', array( $this, \'add_endpoints\' ) );\r\n		add_filter( \'query_vars\', array( $this, \'add_query_vars\' ), 0 );\r\n\r\n		// Change the My Accout page title.\r\n		add_filter( \'the_title\', array( $this, \'endpoint_title\' ) );\r\n\r\n		// Insering your new tab/page into the My Account page.\r\n		add_filter( \'woocommerce_account_menu_items\', array( $this, \'new_menu_items\' ) );\r\n		add_action( \'woocommerce_account_\' . $nameMenu .  \'_endpoint\', array( $this, \'endpoint_content\' ) );\r\n	}\r\n\r\n	/**\r\n	 * Register new endpoint to use inside My Account page.\r\n	 *\r\n	 * @see https://developer.wordpress.org/reference/functions/add_rewrite_endpoint/\r\n	 */\r\n	public function add_endpoints() {\r\n		add_rewrite_endpoint($this->nameMenu,EP_ALL);\r\n	}\r\n\r\n	/**\r\n	 * Add new query var.\r\n	 *\r\n	 * @param array $vars\r\n	 * @return array\r\n	 */\r\n	public function add_query_vars( $vars ) {\r\n		$vars[] = $this->nameMenu;\r\n\r\n		return $vars;\r\n	}\r\n\r\n	/**\r\n	 * Set endpoint title.\r\n	 *\r\n	 * @param string $title\r\n	 * @return string\r\n	 */\r\n	public function endpoint_title( $title ) {\r\n		global $wp_query;\r\n\r\n		$is_endpoint = isset( $wp_query->query_vars[$this->nameMenu] );\r\n\r\n		if ( $is_endpoint && ! is_admin() && is_main_query() && in_the_loop() && is_account_page() ) {\r\n			// New page title.\r\n			$title = __($this->titleMenu, \'woocommerce\' );\r\n\r\n			remove_filter( \'the_title\', array( $this, \'endpoint_title\' ) );\r\n		}\r\n\r\n		return $title;\r\n	}\r\n\r\n	/**\r\n	 * Insert the new endpoint into the My Account menu.\r\n	 *\r\n	 * @param array $items\r\n	 * @return array\r\n	 */\r\n	public function new_menu_items( $items ) {\r\n		// Remove the logout menu item.\r\n		$logout = $items[\'customer-logout\'];\r\n		unset( $items[\'customer-logout\'] );\r\n		// Insert your custom endpoint.\r\n		$items[$this->nameMenu] = __($this->titleMenu, \'woocommerce\' );\r\n\r\n		// Insert back the logout item.\r\n		$items[\'customer-logout\'] = $logout;\r\n\r\n		return $items;\r\n	}\r\n\r\n	/**\r\n	 * Endpoint HTML content.\r\n	 */\r\n	public function endpoint_content() {		 \r\n		 $file_path = WC()->plugin_path() . \'/templates/myaccount/\'.$this->nameMenu.\'.php\';\r\n			if (file_exists($file_path)) {				\r\n				include $file_path;		\r\n			}else{\r\n				$file_to_copy = WC()->plugin_path() . \'/templates/myaccount/template.php\';\r\n				if(copy($file_to_copy, $file_path) ) {\r\n					echo \"File đã được copy thành công!\";\r\n					echo \"Vui lòng cập nhật code theo đường dẫn sau:\".$file_path;\r\n				} else {\r\n					echo \"Có lỗi xảy ra khi thực hiện copy file.\";\r\n				}\r\n			}\r\n	}\r\n\r\n	/**\r\n	 * Plugin install action.\r\n	 * Flush rewrite rules to make our custom endpoint available.\r\n	 */\r\n	public static function install() {\r\n		flush_rewrite_rules();\r\n	}\r\n}\r\n\r\nnew My_Custom_My_Account_Endpoint(\'settings\',\'Cài đặt\');\r\nnew My_Custom_My_Account_Endpoint(\'report\',\'Báo cáo\');\r\nnew My_Custom_My_Account_Endpoint(\'wall\',\'Ví của tôi\');\r\nnew My_Custom_My_Account_Endpoint(\'info\',\'Thông tin\');\r\n// Flush rewrite rules on plugin activation.\r\nregister_activation_hook( __FILE__, array( \'My_Custom_My_Account_Endpoint\', \'install\' ) );','Add Menu Action My Account','','publish','closed','closed','','untitled-snippet','','','2024-05-29 03:31:46','2024-05-29 03:31:46','',0,'https://hamada.vn/wpcode/untitled